#! /usr/bin/rebolREBOL [	Title:		"OpenCV Tests: Reading Video Files"	Author:		"François Jouen"	Rights:		"Copyright (c) 2012-2014 François Jouen. All rights reserved."	License:    "BSD-3 - https://github.com/dockimbel/Red/blob/master/BSD-3-License.txt"]do %../opencv.rset 'appDir what-dir print "Select a movie " ; what we want temp: request-file movie: to-string to-local-file to-string tempcvStartWindowThreadcapture: cvCreateFileCapture movie; some infomation about our movieprint [movie]print ["Width:  " cvGetCaptureProperty capture CV_CAP_PROP_FRAME_WIDTH]print ["Height:  " cvGetCaptureProperty capture CV_CAP_PROP_FRAME_HEIGHT]print ["N of Frames:  " cvGetCaptureProperty capture CV_CAP_PROP_FRAME_COUNT]print ["FPS:  " cvGetCaptureProperty capture CV_CAP_PROP_FPS]cvNamedWindow movie CV_WINDOW_AUTOSIZE ; create window to show the moviefoo: 0while [foo <> 113] [  	image: cvQueryFrame capture ; get frame 	cvShowImage movie image ; show frame	foo: cvWaitKey 42 ; wait for 42 ms  (1/FPS * 1000)]print ["Done. Any key to quit" lf]cvWaitKey 0cvDestroyAllWindowscvReleaseImage imagecvReleaseCapture capture